// notification channel
var WBNotificationCache = function(_auth) {
    WBSocket.on('notification_channel_' + _auth.role, function(data) {
        WBErrors.notification(data.name, data.content, 10);
    });

    WBSocket.on('notification_' + _auth.id, function(data) {
        WBErrors.notification(data.name, data.content, 10);
    });

    // to all employee only
    if (_auth.role !== 'client' || _auth.role !== 'supplier') {
        WBSocket.on('notification_channel_all_employee', function(data) {
            WBErrors.notification(data.name, data.content, 10);
        });
    }
};

// messaging
var WBMessaging = function(_auth) {
    WBSocket.on('message_session_' + _auth.id, function(data) {
        if (parseInt(data.from_id) !== parseInt(jQ('#inbox_from_id').val())) {
            // no in focus play a notification
            WBErrors.notification('New Message', data.message, 10);
        } else {
            WBSocket.emitter().emitEvent('msg_received', [data]);
        }
    });
};

